import React, { useContext } from 'react';
import { ContextGlobal } from './utils/global.context';

const Footer = () => {
  const { state } = useContext(ContextGlobal);

  const iconStyle = {
    filter: state.theme === 'light' ? 'invert(0%)' : 'invert(100%)', // Cambia el color del icono
  };

  return (
    <footer>
        <p>Powered by Junior Lopez</p>
        <img src="/images/DH.png" alt='DH-logo' style={iconStyle} />
          <div>
            <a href="https://www.facebook.com" >
              <img src="/images/ico-facebook.png" style={iconStyle} />
            </a>
            <a href="#" >
              <img src="/images/ico-instagram.png" style={iconStyle} />
            </a>
            <a href="#" >
              <img src="/images/ico-tiktok.png" style={iconStyle} />
            </a>
            <a href="#" >
              <img src="/images/ico-whatsapp.png" style={iconStyle} />
            </a>
          </div>
        
        
    </footer>
  )
}

export default Footer
